CREATE TABLE ACTIVIDAD (ID BIGINT AUTO_INCREMENT NOT NULL, CALIFICACION INTEGER, DESCRIPCION VARCHAR(255), FECHAAPERTURAPLAZAS DATE, FECHACIERREPLAZAS DATE, FECHAPUBLICACION DATE, HORASINVERTIDA DOUBLE, IMAGEN VARCHAR(255), NUMEROPLAZAS INTEGER, TITULO VARCHAR(255), CONVALIDACION_ID BIGINT, ESTADO_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE USUARIO (ID BIGINT AUTO_INCREMENT NOT NULL, DTYPE VARCHAR(31), APELLIDOS VARCHAR(255), CONTRASENA VARCHAR(255), EMAIL VARCHAR(255), NOMBRE VARCHAR(255), TELEFONO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE ADMINISTRADOR (ID BIGINT NOT NULL, PRIMARY KEY (ID))
CREATE TABLE CONVALIDACION (ID BIGINT AUTO_INCREMENT NOT NULL, CREDITOSMAXIMO DOUBLE, DESCRIPCION VARCHAR(255), EVALUACION VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE DEMANDANTE (ID BIGINT NOT NULL, DATOSDEINTERES VARCHAR(255), EDAD INTEGER, PRIMARY KEY (ID))
CREATE TABLE EVENTO (ID BIGINT NOT NULL, DESCRIPCION VARCHAR(255), FECHA DATE, TITULO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE OFERTANTE (ID BIGINT NOT NULL, CALIFICACION DOUBLE, DIRECCION VARCHAR(255), NIF VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE ETIQUETA (NOMBRE VARCHAR(255) NOT NULL, PRIMARY KEY (NOMBRE))
CREATE TABLE ESTADO (ID BIGINT NOT NULL, ESTADOTYPE VARCHAR(255), MENSAJE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE COMENTARIO (ID BIGINT AUTO_INCREMENT NOT NULL, CONTENIDO VARCHAR(255), DEMANDANTE_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE SOLICITUD (ID BIGINT AUTO_INCREMENT NOT NULL, NOTA INTEGER, ESTADO_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE ACTIVIDAD_ETIQUETA (Actividad_ID BIGINT NOT NULL, clasificacion_NOMBRE VARCHAR(255) NOT NULL, PRIMARY KEY (Actividad_ID, clasificacion_NOMBRE))
CREATE TABLE ACTIVIDAD_COMENTARIO (Actividad_ID BIGINT NOT NULL, comentarios_ID BIGINT NOT NULL, PRIMARY KEY (Actividad_ID, comentarios_ID))
CREATE TABLE ACTIVIDAD_EVENTO (Actividad_ID BIGINT NOT NULL, eventos_ID BIGINT NOT NULL, PRIMARY KEY (Actividad_ID, eventos_ID))
CREATE TABLE ACTIVIDAD_SOLICITUD (Actividad_ID BIGINT NOT NULL, solicitudes_ID BIGINT NOT NULL, PRIMARY KEY (Actividad_ID, solicitudes_ID))
CREATE TABLE DEMANDANTE_ETIQUETA (Demandante_ID BIGINT NOT NULL, intereses_NOMBRE VARCHAR(255) NOT NULL, PRIMARY KEY (Demandante_ID, intereses_NOMBRE))
CREATE TABLE DEMANDANTE_SOLICITUD (Demandante_ID BIGINT NOT NULL, solicitudes_ID BIGINT NOT NULL, PRIMARY KEY (Demandante_ID, solicitudes_ID))
CREATE TABLE Ofertante_CONTACTO (Ofertante_ID BIGINT, CONTACTO VARCHAR(255))
CREATE TABLE USUARIO_ACTIVIDAD (Usuario_ID BIGINT NOT NULL, actividades_ID BIGINT NOT NULL, PRIMARY KEY (Usuario_ID, actividades_ID))
